INSERT INTO `peliculas` (`id`, `titulo`, `director`, `sinopsis`, `nacionalidad`, `anyo`, `id_genero`, `poster`) VALUES
(1, 'The Lighthouse', 'Robert Eggers', 'La historia de un guardián del faro llamado Old en la Maine de principios del siglo XX.', 'USA', '2019', 1, 'the lighthouse.jpg'),
(2, 'Jaula', 'Ignacio Tatay', 'Una pareja acoge temporalmente a una niña que encontraron vagando sola por la carretera.', 'ESP', '2022', 1, 'jaula.jpg'),
(3, 'El sastre de la mafia', 'Graham Moore', 'Leonard (Rylance), es un sastre inglés que confeccionaba trajes en la mundialmente famosa Savile Row de Londres.', 'USA', '2022', 1, 'el sastre de la mafia.jpg'),
(4, 'Fractured', 'Brad Anderson', 'Tras la desaparición de su esposa y de su hija herida de las urgencias del hospital, un hombre se embarca en una búsqueda frenética convencido de que le ocultan algo.', 'USA', '2019', 1, 'fractured.jpg'),
(5, 'Midsommar', 'Ari Aster', 'Una joven pareja y sus amigos viajan a Hårga, situado en Hälsingland, una provincia de Suecia, para visitar la ciudad rural de uno de ellos y asistir a su festival de solsticio de verano.', 'USA', '2019', 5, 'midsommar.jpg'),
(6, 'Mother', 'Darren Aronofsky', 'Una pareja vive una existencia aparentemente idílica en su apartada y perfecta casa, hasta que una noche un desconocido, su esposa y sus dos hijos llama a su puerta.', 'USA', '2017', 5, 'mother.jpg'),
(7, 'Alien, el octavo pasajero', 'Ridley Scott', 'La tripulación del remolcador espacial Nostromo atiende una señal de socorro y, sin saberlo, sube a bordo una letal forma de vida extraterrestre.', 'USA', '1978', 5, 'alien.jpg'),
(8, 'La llegada', 'Denis Villeneuve', 'Cuando naves extraterrestres comienzan a llegar a la Tierra, los altos mandos militares piden ayuda a una experta lingüista (Amy Adams) para intentar averiguar si los alienígenas vienen en son de paz o suponen una amenaza.', 'USA', '2016', 2, 'la llegada.jpg'),
(9, 'Chaos walking', 'Doug Liman', 'Año 2267. En un planeta lejano, en la pequeña población de Prentisstown, el joven colono Todd ha crecido creyendo que una raza alienígena asesinó a todas las mujeres y contagió con el \"ruido\" al resto de los hombres.', 'USA', '2021', 2, 'chaos walking.jpg'),
(10, 'Minority report', 'Steven Spielberg', 'racias a una unidad policial de élite, en el año 2054, los crímenes pueden evitarse y es posible detener a los culpables antes de que los cometan.', 'USA', '2002', 2, 'minority report.jpg'),
(11, 'Granujas a todo ritmo', 'John Landis', 'Jake y Elwood deciden volver a tocar con su antigua banda, los Blues Brothers, para ayudar a saldar una deuda del orfanato donde se criaron.', 'USA', '1980', 3, 'granujas a todo ritmo.jpg'),
(12, 'Aterriza como puedas', 'Jim Abrahams', 'Durante un vuelo de Los Angeles a Chicago, gran parte de la tripulación de un avión comercial, incluyendo a los dos pilotos, cae gravemente enferma a consecuencia de una intoxicación alimentaria.', 'USA', '1980', 3, 'aterriza como puedas.jpg'),
(13, 'El Padrino', 'Francis Ford Coppola', 'Don Vito Corleone es el respetado y temido jefe de una de las cinco familias de la mafia de Nueva York en los años 40. ', 'USA', '1972', 4, 'el padrino.jpg'),
(14, 'Doce hombres sin piedad', 'Sidney Lumet', 'Los doce miembros de un jurado deben juzgar a un adolescente acusado de haber matado a su padre. Todos menos uno están convencidos de la culpabilidad del acusado. ', 'USA', '1957', 4, 'doce hombres sin piedad.jpg'),
(15, 'La lista de Schindler', 'Steven Spielberg', 'Oskar Schindler (Liam Neeson), un empresario alemán de gran talento para las relaciones públicas, busca ganarse la simpatía de los nazis de cara a su beneficio personal. ', 'USA', '1993', 4, 'la lista de schindler.jpg'),
(16, 'El pianista', 'Roman Polanski', 'Wladyslaw Szpilman, un brillante pianista polaco de origen judío, vive con su familia en el ghetto de Varsovia. ', 'UK', '2002', 6, 'el pianista.jpg'),
(17, '1917', 'Sam Mendes', 'En lo más crudo de la Primera Guerra Mundial, dos jóvenes soldados británicos, Schofield (George MacKay) y Blake (Dean-Charles Chapman) reciben una misión aparentemente imposible.', 'UK', '2019', 6, '1917.jpg'),
(18, 'Dunkerque', 'Christopher Nolan', 'Año 1940, en plena 2ª Guerra Mundial. En las playas de Dunkerque, cientos de miles de soldados de las tropas británicas y francesas se encuentran rodeados por el avance del ejército alemán, que ha invadido Francia.', 'UK', '2017', 6, 'dunkerque.jpg'),
(19, 'Llaman a la puerta', 'M. Night Shyamalan', 'Una de la hostia', 'USA', '2023', 5, 'llaman a la puerta.jpg');